(function($) {
    "use strict";
    $("[data-bg-image]").each(function() { $(this).css("background-image", "url(" + $(this).attr("data-bg-image") + ")") });
    $("[data-bg-color]").each(function() { $(this).css("background-color", $(this).attr("data-bg-color")) });
    $(document).ready(function($) {
        var lastScrollTop = 0;
        $(window).scroll(function() {
            var scroll = $(window).scrollTop();
            if (scroll > 300) { $(".tj-header-area.header-sticky").addClass("sticky") } else { $(".tj-header-area.header-sticky").removeClass("sticky") }
            if (scroll > lastScrollTop && scroll > 300) { $(".tj-header-area.header-sticky").addClass("sticky-down") } else { $(".tj-header-area.header-sticky").removeClass("sticky-down") }
            lastScrollTop = scroll
        });
        $(".hamburger-menu").on("click", function() {
            $(".side-mobile-menu").addClass("active");
            $("body").addClass("mobile-menu-active")
        });
        $(".mobile-menu-close").on("click", function() {
            $(".side-mobile-menu").removeClass("active");
            $("body").removeClass("mobile-menu-active")
        });
        $(".side-mobile-menu ul li a").on("click", function() {
            $(".side-mobile-menu").removeClass("active");
            $("body").removeClass("mobile-menu-active")
        });
        $('a[href*="#"]:not([href="#"])').click(function() {
            if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
                var target = $(this.hash);
                target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
                if (target.length) { $('html,body').animate({ scrollTop: target.offset().top - 80 }, 1000); return false }
            }
        });
        $(".tj-one-page-nav ul li a").on("click", function() {
            $(".tj-one-page-nav ul li").removeClass("active");
            $(this).parent().addClass("active")
        });
        var $grid = $('.tj-portfolio-gallery').isotope({ itemSelector: '.tj-portfolio-item', layoutMode: 'fitRows' });
        $('.tj-portfolio-filter button').on('click', function() {
            var filterValue = $(this).attr('data-filter');
            $grid.isotope({ filter: filterValue });
            $('.tj-portfolio-filter button').removeClass('active');
            $(this).addClass('active')
        });
        $('.tj-testimonial-carousel').owlCarousel({ loop: true, margin: 30, items: 1, autoplay: true, autoplayTimeout: 5000, autoplayHoverPause: true, nav: false, dots: true, responsive: { 0: { items: 1 }, 768: { items: 2 }, 992: { items: 3 } } });
        $('select').niceSelect();
        $('.tj-portfolio-gallery').magnificPopup({ delegate: 'a', type: 'image', gallery: { enabled: true }, zoom: { enabled: true, duration: 300 } });
        $('.tj-sidebar-menu ul li a').hover(function() { $(this).parent().addClass('active') }, function() { $(this).parent().removeClass('active') });
        $('.tj-services-item').hover(function() { $(this).addClass('active') }, function() { $(this).removeClass('active') });
        $('.tj-portfolio-filter button').hover(function() { $(this).addClass('active') }, function() { $(this).removeClass('active') });
        $('.tj-funfact-item').each(function() {
            var $this = $(this);
            var countTo = $this.attr('data-count');
            $({ countNum: $this.find('.tj-funfact-number').text() }).animate({ countNum: countTo }, { duration: 2000, easing: 'linear', step: function() { $this.find('.tj-funfact-number').text(Math.floor(this.countNum)) }, complete: function() { $this.find('.tj-funfact-number').text(this.countNum) } })
        });
        if (typeof WOW !== 'undefined') { new WOW().init() }
    });
})(jQuery);